import React, {Component} from 'react';
import Button from 'react-bootstrap/Button';
import ReactTable from 'react-table';
import 'react-table/react-table.css'
import './candidates.css';

class Candidates extends Component{
    constructor() {
        super();
        this.state = {
            candidates: [],
            selected:{}
        }
    }

    componentDidMount() {
        fetch('/voting-app/candidates/',{
            method: "GET"})
            .then(res => res.json())
            .then(candidates => this.setState({candidates}, () => console.log('Candidates fetched..',
            candidates)));
    }

    render(){

        const columns = [
            {
                Header: "ID",
                accessor: "Record.ID"
            },
            {
                Header: "Name",
                accessor: "Record.Name"
            },
            {
                Header: "Party",
                accessor: "Record.Description"
            },
            {
                Header: "Age",
                accessor: "Record.Age"
            }
        ]

        return (
            <div>
                <ReactTable
                    className="-striped -highlight"
                    minRows={1}
                    columns={columns}
                    data={this.state.candidates}
                    getTdProps={(state, rowInfo, column) => {
                        if (rowInfo !== undefined) {
                          return {
                            onClick: (event, handleOriginal) => {
                              event.stopPropagation();
                              if (event.altKey)
                                this.onRowClick("altClick", rowInfo.row._original);
                              else if (event.shiftKey)
                                this.onRowClick(
                                  "shiftClick",
                                  rowInfo.row._original,
                                  rowInfo.index
                                );
                              else this.onRowClick("click", rowInfo.row._original);
                              if (handleOriginal) handleOriginal();
                            }
                          };
                        }
                      }}
                >

                </ReactTable>

            <Button variant="success">Success</Button>
            </div>

        );
    }
}

export default Candidates;
