import React, {Component} from 'react';
import Button from 'react-bootstrap/Button';
import ReactTable from 'react-table';
import 'react-table/react-table.css'
import './candidates.css';

class Candidates extends Component{
    constructor() {
        super();
        this.state = {
            candidates: [],
            vote: null,
        }
    }

    componentDidMount() {
        fetch('/voting-app/candidates/',{
            method: "GET"})
            .then(res => res.json())
            .then(candidates => this.setState({candidates}, () => console.log('Candidates fetched..',
            candidates)));
    }

    render(){

        const columns = [
            {
                Header: "Vote",
                style:{
                      textAlign: "center"  
                },
                Cell: props =>{
                    return(
                        <div class="radio">
                            <label><input type="radio" name="optradio"
                                onClick={() =>{

                                    this.state.vote = props.original.Record.Vote;
                                    console.log(this.state.vote);
                                }}
                            /></label>
                        </div>
                        /*<Button variant="success"
                            onClick={() =>{
                                //console.log("propts", props.original.Record.Vote);
                                this.state.vote = props.original.Record.Vote;
                                console.log(this.state.vote);
                            }}
                        >Vote</Button>*/
                    )
                },
                sortable: false,
                width: 100,
                maxWidth:100,
                minWidth:100
            },
            {
                Header: "ID",
                accessor: "Record.ID",
                style:{
                      textAlign: "center"  
                },
                width: 100,
                maxWidth:100,
                minWidth:100
            },
            {
                Header: "Name",
                accessor: "Record.Name",
                style:{
                    textAlign: "center"  
                }

            },
            {
                Header: "Party",
                accessor: "Record.Description",
                style:{
                    textAlign: "center"  
                },
                filterable: false
            },
            {
                Header: "Age",
                accessor: "Record.Age",
                style:{
                    textAlign: "center"  
                },
                filterable: false
            }
        ]

        return (
            <div>
                <ReactTable
                    className="-striped -highlight"
                    defaultPageSize={5}
                    minRows={1}
                    columns={columns}
                    data={this.state.candidates}
                >
                </ReactTable>
                <Button variant="success"
                    onClick={() =>{
                        console.log(this.state.vote);
                    }}
                />
            </div>

        );
    }
}

export default Candidates;
