import React, {Component} from 'react';
import Button from 'react-bootstrap/Button';
import ReactTable from 'react-table';
import 'react-table/react-table.css';
import './candidates.css';
import { Checkbox } from '@shopify/polaris';

class Candidates extends Component{
    constructor() {
        super();
        this.state = {
            candidates: []
        }
    }

    componentDidMount() {
        fetch('/voting-app/candidates/',{
            method: "GET"})
            .then(res => res.json())
            .then(candidates => this.setState({candidates}, () => console.log('Candidates fetched..',
            candidates)));
    }

    render(){
        const columns = [
            {
                id: "checkbox",
                            accessor: "",
                            Cell: ( rowInfo ) => {
                                return (
                                    <Checkbox
                                        type="checkbox"
                                        className="checkbox"
                                      checked={this.state.selected[rowInfo.original.title.props.children] === true}
                                        onChange={() => this.toggleRow(rowInfo.original.title.props.children)}
                                    />
                                );
                            },
                Header: "Name",
                accessor: "Record.Name"
            },
            {
                Header: "Description",
                accessor: "Record.Description"
            }
        ]
        return (
            <div>
                <ReactTable
                    columns={columns}
                    data={this.state.candidates}
                >

                </ReactTable>

            <Button variant="success">Success</Button>
            </div>

        );
    }
}

export default Candidates;
