import React, {Component} from 'react';
import Button from 'react-bootstrap/Button';
import ReactTable from 'react-table';
import 'react-table/react-table.css';
import './candidates.css';

class Candidates extends Component{
    constructor() {
        super();
        this.state = {
            candidates: []
        }
    }

    componentDidMount() {
        fetch('/voting-app/candidates/',{
            method: "GET"})
            .then(res => res.json())
            .then(candidates => this.setState({candidates}, () => console.log('Candidates fetched..',
            candidates)));
    }

    render(){
        const columns = [
            {
                Header: "Name",
                accessor: "Name"
            },
            {
                Header: "Description",
                accessor: "Description"
            }
        ]
        return (
            <div>
              <ul>
                    {this.state.candidates.map(candidate =>
                       <li key={candidate.Key}>{ candidate.Record.Name } { candidate.Record.Description }</li>
                    )} 
                </ul>
                <ReactTable
                    columns={columns}
                    data={this.state.candidates.Record}
                >

                </ReactTable>

            <Button variant="success">Success</Button>
            </div>

        );
    }
}

export default Candidates;
