/* eslint-disable no-unused-vars */
/* eslint-disable react/no-direct-mutation-state */
/* eslint-disable no-undef */
/* eslint-disable no-unused-expressions */
import React, {Component} from 'react';
import Button from 'react-bootstrap/Button';
import ReactTable from 'react-table';
import axios from 'axios';
import 'react-table/react-table.css'
import './candidates.css';

const { encryptWithProof, verifyProof } = require('paillier-in-set-zkp')
const paillier = require('paillier-js')
var bigInt = require("big-integer");

class Candidates extends Component{

    constructor() {
        super();
        this.state = {
            pubKey: [],
            candidates: [],
            vote: null,
            voteName: '',
            bits: null
        }
        this.handleOnClickVote = this.handleOnClickVote.bind(this);
        this.handlerVote = this.handlerVote.bind(this);
    }

    componentDidMount() {
        fetch('/voting-app/candidates/',{
            method: "GET"})
            .then(res => res.json())
            .then(candidates => this.setState({candidates}, () => console.log('Candidates fetched..',
            candidates)));
        fetch('/voting-app/getPubKey/',{
            method: "GET"})
            .then(res => res.json())
            .then(pubKey => this.setState({pubKey}, () => console.log('Public Key fetched..',
            pubKey)));
        fetch('/voting-app/getBits/',{
            method: "GET"})
            .then(res => res.json())
            .then(bits => this.setState({bits}, () => console.log('Number of bits fetched..',
            bits)));

    }

    handleOnClickVote(){
        var validScores = this.state.candidates.map(function (obj) {
            return obj.Record.Vote;
        });
        console.log(validScores);
        console.time('encrypt');

        const publicKey = new paillier.PublicKey(bigInt(this.state.pubKey.n), this.state.pubKey.g);

        const [cipher, proof, random] = encryptWithProof(publicKey, this.state.vote, validScores, this.state.bits);
        console.log(cipher);
        console.log(proof);
        console.log(random.toString());
        console.timeEnd('encrypt');
        var vote = {
            id: '1',
            Vote: parseInt(this.state.vote)
        }
        console.log(this.state.voteName);
        axios.post('/voting-app/vote', vote)
            .then(response => {
                console.log(response);
            })
            .catch(error => {
                console.log(error);
            })
    }

    handlerVote(){

        return (
            <p>test</p>
        );
    }

    render(){
        const columns = [
            {
                Header: "Vote",
                style:{
                      textAlign: "center"  
                },
                Cell: props =>{
                    return(
                        <div class="radio">
                            <label><input type="radio" name="optradio"
                                onClick={() =>{
                                    this.state.vote = props.original.Record.Vote;
                                    this.state.voteName = props.original.Record.Name;
                                }}
                            /></label>
                        </div>
                    )
                },
                sortable: false,
                width: 100,
                maxWidth:100,
                minWidth:100
            },
            {
                Header: "ID",
                accessor: "Record.ID",
                style:{
                      textAlign: "center"  
                },
                width: 75,
                maxWidth: 75,
                minWidth: 75
            },
            {
                Header: "Name",
                accessor: "Record.Name",
                style:{
                    textAlign: "center"  
                }

            },
            {
                Header: "Party",
                accessor: "Record.Description",
                style:{
                    textAlign: "center"  
                },
                filterable: false
            },
            {
                Header: "Age",
                accessor: "Record.Age",
                style:{
                    textAlign: "center"  
                },
                filterable: false,
            }
        ]

        return (
            <div>
                <ReactTable
                    className="-striped -highlight"
                    defaultPageSize={5}
                    minRows={1}
                    columns={columns}
                    data={this.state.candidates}
                >
                </ReactTable>

                <Button variant="success" onClick={this.handleOnClickVote}>Vote</Button>
                {this.handlerVote}
            </div>

        );
    }
}

export default Candidates;
