import React, {Component} from 'react';
import Button from 'react-bootstrap/Button';
import Table from 'react-bootstrap/Table';
import './candidates.css';

class Candidates extends Component{
    constructor() {
        super();
        this.state = {
            candidates: []
        }
    }

    componentDidMount() {
        fetch('/voting-app/candidates/',{
            method: "GET"})
            .then(res => res.json())
            .then(candidates => this.setState({candidates}, () => console.log('Candidates fetched..',
            candidates)));
    }

    onSiteChanged (e) {
        this.setState({
          site: e.currentTarget.value
          });
      }

      onAddressChanged (e) {
        this.setState({
          address: e.currentTarget.value
          });
      }

    render(){
        var resultRows = this.props.data.map(function(result){
            return (
                <tbody>
                     <tr>
                         <td><input type="radio" name="site_name" 
                                    value={result.SITE_NAME} 
                                    checked={this.state.candidate.Record.Name === result.SITE_NAME} 
                                    onChange={this.onSiteChanged} />{result.SITE_NAME}</td>
                         <td><input type="radio" name="address" 
                                    value={result.ADDRESS}  
                                    checked={this.state.candidate.Record.Description === result.ADDRESS} 
                                    onChange={this.onAddressChanged} />{result.ADDRESS}</td>
                     </tr>
                </tbody>
            );
        }, this);
        return (
            <div>
              <ul>
                    {this.state.candidates.map(candidate =>
                       <li key={candidate.Key}>{ candidate.Record.Name } { candidate.Record.Description }</li>
                    )} 
                </ul>
                <table className="table">
               <thead>
                   <tr>
                       <th>Name</th>
                       <th>Address</th>
                   </tr>
               </thead>
                {resultRows}
               <tfoot>
                   <tr>
                       <td>chosen site name {this.state.site} </td>
                       <td>chosen address {this.state.address} </td>
                   </tr>
               </tfoot>
           </table>
            <Button variant="success">Success</Button>
            </div>

        );
    }
}

export default Candidates;
