import React, {Component} from 'react';
import Button from 'react-bootstrap/Button';
import ReactTable from 'react-table';
import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import 'react-table/react-table.css'
import './candidates.css';

class Candidates extends Component{
    constructor() {
        super();
        this.state = {
            candidates: [],
            selected:{}
        }
    }

    componentDidMount() {
        fetch('/voting-app/candidates/',{
            method: "GET"})
            .then(res => res.json())
            .then(candidates => this.setState({candidates}, () => console.log('Candidates fetched..',
            candidates)));
    }

    render(){

        const columns = [
            {
                Header: "ID",
                accessor: "Record.ID"
            },
            {
                Header: "Name",
                accessor: "Record.Name"
            },
            {
                Header: "Party",
                accessor: "Record.Description"
            },
            {
                Header: "Age",
                accessor: "Record.Age"
            }
        ]

        return (
            <div>
                <ReactTable
                    className="-striped -highlight"
                    minRows={1}
                    columns={columns}
                    data={this.state.candidates}
                >

                </ReactTable>
                <BootstrapTable data={this.state.candidates} striped hover>
      <TableHeaderColumn isKey dataField='Record.ID'>Product ID</TableHeaderColumn>
      <TableHeaderColumn dataField='Record.Name'>Product Name</TableHeaderColumn>
  </BootstrapTable>,
            <Button variant="success">Success</Button>
            </div>

        );
    }
}

export default Candidates;
